var link=document.querySelector(".contacts-button"),close=document.querySelector(".modal-close"),popup=document.querySelector(".modal-message"),people=popup.querySelector("[name=name]"),email=popup.querySelector("[name=email]"),overlay=document.querySelector(".overlay"),form=popup.querySelector("form");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),overlay.classList.add("overlay-show"),people.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show"),overlay.classList.remove("overlay-show"),popup.classList.remove("modal-error")}),overlay.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show"),overlay.classList.remove("overlay-show"),popup.classList.remove("modal-error")}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),popup.classList.contains("modal-show")&&(popup.classList.remove("modal-show"),popup.classList.remove("modal-error")),overlay.classList.contains("overlay-show")&&overlay.classList.remove("overlay-show"))}),form.addEventListener("submit",function(e){people.value&&email.value||(e.preventDefault(),popup.classList.add("modal-error"))});